func:
switch (msg.topic) {
  case "get/ESP8266/counterValue":
    context.filterValue = context.filterValue || 0;
    var filterConstant = context.filterConstant || 0.4;
    var unfiltered = msg.payload;
    var filtered = ((unfiltered - context.filterValue) * filterConstant) + context.filterValue;
    context.filterValue = Math.round(filtered);
    msg.topic = "get/ESP8266/filterValue";
    msg.payload = context.filterValue;
    return msg;
    break;
  case "set/ESP8266/filterConstant":
    if (msg.payload >= 0.0 && msg.payload <= 1.0) {
      context.filterConstant = msg.payload;
      msg.topic = "get/ESP8266/filterConstant";
      msg.payload = context.filterConstant;
      msg.retain = true;
      return msg;
    }
    break;
}

return null;
